tinymce.PluginManager.add('dotimageclipboard', function(editor, url) {
    // Add a button that opens a window
    editor.ui.registry.addButton("dotimageclipboard", {
			// image : url + '/img/image-clipboard.png',
			text: "",
			icon: "paste",
			tooltip: "Clipboard",
			onAction: function () {
				editor.windowManager.openUrl({
					title: "DotCMS Image Clipboard",
					url: url + "/dialog.jsp",
					width: 550,
					height: 460,
				});
			},
		});
    // Adds a menu item to the tools menu
    editor.ui.registry.addMenuItem("dotimageclipboard", {
			text: "DotImageClipboard Plugin",
      context: "tools",
      onAction: () => {
        editor.windowManager.openUrl({
					title: "DotCMS Image Clipboard",
					url: url + "/dialog.jsp",
					width: 550,
					height: 460,
				});		   
      }
		});
});